import java.util.Iterator;

public class forEach {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		
		String jugadores[]={"iniesta","Torres","Capdevila","Xavi","Casillas","Pedrito","Sergio Ramos",
				"Xavi","Xavi","Villa","Puyol","Pique","Cesc","Silva","Cazorla","Jordi Alba"};
		
		int contador=0;
		for (String miau : jugadores) 
		{contador ++;
			System.out.println(contador + miau );
			
		}

		

			
			
				
		
	}

}
